import React, { Component } from "react";
import SVG from "react-inlinesvg";
import BootstrapTable from 'react-bootstrap-table-next';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import paginationFactory from 'react-bootstrap-table2-paginator';
import './User.css';
import PropTypes from "prop-types";
import { connect } from "react-redux";
//import store from "../../store";
import jwt_decode from "jwt-decode";
import { logoutUser } from "../../../actions/authActions";
import { listUsers } from "../../../actions/usersActions";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


function StatusColumnFormatter() {
    //const CustomerStatusCssClasses = ["danger", "success", "warning", "info"];
    //const CustomerStatusTitles = ["Suspended", "Active", "Pending", "Inactive"];
    const getLabelCssClasses = () => {
        return `status-label status-light-${'info'}`;
    };
    return (
        <span className={getLabelCssClasses()}>
            Inactive
        </span>
    );
}
function ActionsColumnFormatter() {
    const toAbsoluteUrl = pathname => process.env.PUBLIC_URL + pathname;
    return (
        <>
            <a title="Edit" className="btn btn-icon btn-light btn-hover-primary btn-sm">
                <span className="svg-icon svg-icon-md svg-icon-primary">
                    <SVG
                        src={toAbsoluteUrl("/assets/svg/icons/Communication/Write.svg")}
                    />
                </span>
            </a>
            <a title="Delete" className="btn btn-icon btn-light btn-hover-danger btn-sm" >
                <span className="svg-icon svg-icon-md svg-icon-danger">
                    <SVG src={toAbsoluteUrl("/assets/svg/icons/General/Trash.svg")} />
                </span>
            </a>
        </>
    );
}

// function SelectionCheckbox({ isSelected, onChange }) {
//     return (
//         <>
//             <input type="checkbox" style={{ display: "none" }} />
//             <label className="checkbox checkbox-single">
//                 <input type="checkbox" checked={isSelected} onChange={onChange} />
//                 <span />
//             </label>
//         </>
//     );
// }

class UserList extends Component {
    constructor() {
        super();
    }

    componentDidMount() {
    };


    render() {
        const columns = [
            { dataField: "id", text: "S.No", sort: true },
            { dataField: "empId", text: "Emp ID", sort: true },
            { dataField: "name", text: "Name", sort: true },
            { dataField: "email", text: "Email", sort: false },
            { dataField: "skill", text: "Skill", sort: false },
            { dataField: "userType", text: "User Type", sort: true },
            // { dataField: "userType", text: "User Type", classes: "list-status-td", sort: true, formatter: StatusColumnFormatter },
            { dataField: "action", text: "Actions", classes: "text-right table-card-action", formatter: ActionsColumnFormatter, headerClasses: "text-right", style: { minWidth: "100px", } },
        ];

        const products = [
            { id: 1, empId: 'Arka123', name: 'super', email: 'abc@gmail.com', skill: 'react', userType:'admin', action: { ActionsColumnFormatter } },
        ];

        const defaultSorted = [{
            dataField: 'id',
            order: 'asc'
        }];

        const customTotal = (from, to, size) => (
            <span className="react-bootstrap-table-pagination-total">
                Showing rows { from} to { to} of { size}
            </span>
        );

        // Table pagination properties
        const paginationOptions = {
            paginationSize: 10,
            pageStartIndex: 0,
            //custom: true,
            firstPageText: 'First',
            prePageText: 'Back',
            nextPageText: 'Next',
            lastPageText: 'Last',
            nextPageTitle: 'First page',
            prePageTitle: 'Pre page',
            firstPageTitle: 'Next page',
            lastPageTitle: 'Last page',
            showTotal: true,
            paginationTotalRenderer: customTotal,
            sizePerPageList: [{
                text: '10', value: 10
            }, {
                text: '20', value: 20
            }, {
                text: 'All', value: products.length
            }],
        };

        const selectRow = {
            mode: 'checkbox',
            clickToSelect: true,
            clickToEdit: true,
            hideSelectAll: false,

            // selectionHeaderRenderer: () => {
            //     return (
            //         <SelectionCheckbox />
            //     );
            // },
        };

        return (
            <>
                <div className="d-flex flex-row flex-column-fluid page">
                    <div className="d-flex flex-column flex-row-fluid wrapper">
                        <div class="container">
                            <div className="table-card">
                                <div class="table-card-header"><div class="table-card-title"><h3 class="card-label">Users list</h3></div>
                                    <div class="table-card-toolbar">
                                        <button type="button" class="btn btn-primary">Add User</button>
                                    </div>
                                </div>
                                <div className="table-card-body">
                                    <BootstrapTable
                                        selectRow={selectRow}
                                        bordered={false}
                                        classes="table table-head-custom table-vertical-center overflow-hidden"
                                        bootstrap4
                                        keyField='id'
                                        data={products}
                                        columns={columns}
                                        defaultSorted={defaultSorted}
                                        pagination={paginationFactory(paginationOptions)}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>

        );
    }
}
export default UserList;
