class UserList extends Component {
    constructor() {
        super();
    }

    componentDidMount() {
    };


    render() {
        const columns = [
            { dataField: "id", text: "ID", sort: true },
            { dataField: "customer", text: "Customer", sort: true },
            { dataField: "country", text: "Country", sort: true },
            { dataField: "shipDate", text: "Ship Date", sort: true },
            { dataField: "companyName", text: "Company Name", sort: false },
            { dataField: "status", text: "Status", classes: "list-status-td", sort: true, formatter: StatusColumnFormatter },
            { dataField: "action", text: "Actions", classes: "text-right table-card-action", formatter: ActionsColumnFormatter, headerClasses: "text-right", style: { minWidth: "100px", } },
        ];

        const products = [
            { id: 1, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 2, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 3, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 4, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 5, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 6, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 7, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 8, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 9, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 10, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 11, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 12, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 13, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 14, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 15, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 16, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } },
            { id: 17, customer: 'Ora Zanassi', country: 'Indonesia', shipDate: '6/25/2016', companyName: 'Arka Softwares', status: { StatusColumnFormatter }, action: { ActionsColumnFormatter } }
        ];

        const defaultSorted = [{
            dataField: 'id',
            order: 'asc'
        }];

        const customTotal = (from, to, size) => (
            <span className="react-bootstrap-table-pagination-total">
                Showing rows { from} to { to} of { size}
            </span>
        );

        // Table pagination properties
        const paginationOptions = {
            paginationSize: 10,
            pageStartIndex: 0,
            //custom: true,
            firstPageText: 'First',
            prePageText: 'Back',
            nextPageText: 'Next',
            lastPageText: 'Last',
            nextPageTitle: 'First page',
            prePageTitle: 'Pre page',
            firstPageTitle: 'Next page',
            lastPageTitle: 'Last page',
            showTotal: true,
            paginationTotalRenderer: customTotal,
            sizePerPageList: [{
                text: '10', value: 10
            }, {
                text: '20', value: 20
            }, {
                text: 'All', value: products.length
            }],
        };

        const selectRow = {
            mode: 'checkbox',
            clickToSelect: true,
            clickToEdit: true,
            hideSelectAll: false,

            // selectionHeaderRenderer: () => {
            //     return (
            //         <SelectionCheckbox />
            //     );
            // },
        };

        return (
            <>
                <div className="d-flex flex-row flex-column-fluid page">
                    <div className="d-flex flex-column flex-row-fluid wrapper">
                        <div class="container">
                            <div className="table-card">
                                <div class="table-card-header"><div class="table-card-title"><h3 class="card-label">Customers list</h3></div>
                                    <div class="table-card-toolbar">
                                        <button type="button" class="btn btn-primary">New Customer</button>
                                    </div>
                                </div>
                                <div className="table-card-body">
                                    <BootstrapTable
                                        selectRow={selectRow}
                                        bordered={false}
                                        classes="table table-head-custom table-vertical-center overflow-hidden"
                                        bootstrap4
                                        keyField='id'
                                        data={products}
                                        columns={columns}
                                        defaultSorted={defaultSorted}
                                        pagination={paginationFactory(paginationOptions)}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>

        );
    }
}
export default UserList;
