import React, { Component } from "react";
import SVG from "react-inlinesvg";
import BootstrapTable from 'react-bootstrap-table-next';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import paginationFactory from 'react-bootstrap-table2-paginator';
import './User.css';
import PropTypes from "prop-types";
import { connect } from "react-redux";
import store from "../../../store";
import jwt_decode from "jwt-decode";
import { logoutUser } from "../../../actions/authActions";
import { listUsers } from "../../../actions/usersActions";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

var usersData = [];
var skillData = [];

function StatusColumnFormatter() {
    //const CustomerStatusCssClasses = ["danger", "success", "warning", "info"];
    //const CustomerStatusTitles = ["Suspended", "Active", "Pending", "Inactive"];
    const getLabelCssClasses = () => {
        return `status-label status-light-${'info'}`;
    };
    return (
        <span className={getLabelCssClasses()}>
            Inactive
        </span>
    );
}
function ActionsColumnFormatter() {
    const toAbsoluteUrl = pathname => process.env.PUBLIC_URL + pathname;
    return (
        <>
            <a title="Edit" className="btn btn-icon btn-light btn-hover-primary btn-sm">
                <span className="svg-icon svg-icon-md svg-icon-primary">
                    <SVG
                        src={toAbsoluteUrl("/assets/svg/icons/Communication/Write.svg")}
                    />
                </span>
            </a>
            <a title="Delete" className="btn btn-icon btn-light btn-hover-danger btn-sm" >
                <span className="svg-icon svg-icon-md svg-icon-danger">
                    <SVG src={toAbsoluteUrl("/assets/svg/icons/General/Trash.svg")} />
                </span>
            </a>
        </>
    );
}

