class UserList extends Component {
    constructor() {
        super();
        this.state = {
            token: "",
            projectItem: "",
            filter: "",  
        };
    }

    componentDidMount(){
        if(localStorage.jwtToken)
        { 
          const token = localStorage.jwtToken;
          const decoded = jwt_decode(token);

          this.setState({ token: decoded.token });

          const userData = {
            user_type: 1,
            token: decoded.token
          };

          this.listUser(userData);
        }else{
          store.dispatch(logoutUser());
          this.props.history.push("/login");
        }
      };

      componentWillReceiveProps(nextProps) {
          console.log('next prop',nextProps)
        skillData = [];
        if (nextProps.apiRes) {
          if(nextProps.apiRes.status === 200)
          {
            if(nextProps.apiRes.data.message == null)
            {
              usersData = [];

              var data = nextProps.apiRes.data;
              //console.log(data)
              for (var i = 0; i < data.length; i++) {
                data[i].skills.map((item)=>{
                  //console.log('data skill',item)

                    var skillArr = {
                      proficiency: item.proficiency,
                      skillname: item.technology.name
                    }
                    skillData.push(skillArr)   
                })
                var j = i+1;
                var dataArr = {
                  s_no : j,
                  name : data[i].name,
                  user_id : data[i].id,
                  emp_id : data[i].employee_code,
                  email : data[i].email,
                  //skill : data[i].skills,
                  user_type: data[i].user_type,  
                  //action:'efvgbthrnb'
                }

                usersData.push(dataArr);

              }
             console.log('user data is',usersData)
             console.log('skill',skillData)

            }else{
              toast.success(nextProps.apiRes.data.message, {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });

            //   const userData = {
            //     user_type: 1,
            //     token: this.state.token
            //   };

            //   this.listUser(userData);
            }
          }else{
            toast.error(nextProps.apiRes.errorMessage, {
              position: "top-right",
              autoClose: 5000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
          }
        }
      }

      listUser(userData)
      {
        this.props.listUsers(userData);
      }

      onChange = e => {
        console.log('normal data',usersData);
         //this.setState({ [e.target.name]: e.target.value });
         this.setState({ filter: e.target.value });
     };

     skillCond(usersData){
         console.log('skill dattaaa',usersData)
        if(skillData.length > 0){ 
          return skillData.map((val)=>{

             return <p>{val.technology.name} - <span>{val.proficiency}</span></p>;
           })
         }
          else{
            return <p>No Data</p>
          } 
    }


    render() {
        const columns = [
            { dataField: "s_no", text: "S.No", sort: true },
            { dataField: "emp_id", text: "Emp ID", sort: true },
            { dataField: "name", text: "Name", sort: true },
            { dataField: "email", text: "Email", sort: false },
            { dataField: "skill", text: "Skill", sort: false, formatter: this.skillCond },
            { dataField: "user_type", text: "User Type", sort: true },
            // { dataField: "userType", text: "User Type", classes: "list-status-td", sort: true, formatter: StatusColumnFormatter },
            { dataField: "action", text: "Actions", classes: "text-right table-card-action", formatter: ActionsColumnFormatter, headerClasses: "text-right", style: { minWidth: "100px", } },
        ];

        // const products = [
        //     { s_no: 1, emp_id: 'Arka123', name: 'super', email: 'abc@gmail.com', skill: 'react', userType:'admin', action: { ActionsColumnFormatter } },
        //     { s_no: 1, emp_id: 'Arka123', name: 'super', email: 'abc@gmail.com', skill: 'react', userType:'admin', action: { ActionsColumnFormatter } },
        // ];

        const defaultSorted = [{
            dataField: 'id',
            order: 'asc'
        }];

        const customTotal = (from, to, size) => (
            <span className="react-bootstrap-table-pagination-total">
                Showing rows { from} to { to} of { size}
            </span>
        );

        // Table pagination properties
        const paginationOptions = {
            paginationSize: 10,
            pageStartIndex: 0,
            //custom: true,
            firstPageText: 'First',
            prePageText: 'Back',
            nextPageText: 'Next',
            lastPageText: 'Last',
            nextPageTitle: 'First page',
            prePageTitle: 'Pre page',
            firstPageTitle: 'Next page',
            lastPageTitle: 'Last page',
            showTotal: true,
            paginationTotalRenderer: customTotal,
            sizePerPageList: [{
                text: '10', value: 10
            }, {
                text: '20', value: 20
            }, {
                text: 'All', value: usersData.length
            }],
        };

        const selectRow = {
            mode: 'checkbox',
            clickToSelect: true,
            clickToEdit: true,
            hideSelectAll: false,

            // selectionHeaderRenderer: () => {
            //     return (
            //         <SelectionCheckbox />
            //     );
            // },
        };

        return (
            <>
                <div className="d-flex flex-row flex-column-fluid page">
                    <div className="d-flex flex-column flex-row-fluid wrapper">
                        <div class="container">
                            <div className="table-card">
                                <div class="table-card-header"><div class="table-card-title"><h3 class="card-label">Users list</h3></div>
                                    <div class="table-card-toolbar">
                                        <button type="button" class="btn btn-primary">Add User</button>
                                    </div>
                                </div>
                                <div className="table-card-body">
                                    {/* {console.log( products)} */}
                                    <BootstrapTable
                                        // selectRow={selectRow}
                                        bordered={false}
                                        classes="table table-head-custom table-vertical-center overflow-hidden"
                                        bootstrap4
                                        keyField='id'
                                        data={usersData}
                                        columns={columns}
                                        defaultSorted={defaultSorted}
                                        pagination={paginationFactory(paginationOptions)}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>

        );
    }
}


UserList.propTypes = {
    listUsers: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    apiRes: PropTypes.object.isRequired
  };

  const mapStateToProps = state => ({
    auth: state.auth,
    apiRes: state.apiRes
  });

  export default connect(
    mapStateToProps,
    { listUsers }
  )(UserList);
